mapper.select:
	SELECT USER_ID, PW 
    FROM USERS 
    WHERE USER_ID = #{userid} AND PW = #{password}

SELECT COUNT(*) 
FROM USERS 
WHERE USER_ID=#{userId}

SELECT COUNT(*) 
FROM USERS 
WHERE NICKNAME=#{nickname}

SELECT USER_ID 
FROM USERS 
WHERE USER_NAME=#{userName} AND PHONE_NUMBER=#{phoneNumber}

mapper.update:
UPDATE USERS 
SET PW=#{password} 
WHERE USER_ID=#{userId}
