mapper.select:
SELECT COUNT(*)
FROM COMMUNITY
JOIN USERS ON COMMUNITY.USER_ID = USERS.USER_ID

	WHERE COMMUNITY.TITLE LIKE #{keyword}
	OR COMMUNITY.TAG LIKE #{keyword}
	OR USERS.NICKNAME LIKE #{keyword}

SELECT COUNT(*)
FROM COMMUNITY
JOIN USERS ON COMMUNITY.USER_ID = USERS.USER_ID


        (COMMUNITY.TITLE LIKE #{keyword}
        OR COMMUNITY.TAG LIKE #{keyword}
        OR USERS.NICKNAME LIKE #{keyword})


        AND COMMUNITY.TAG = #{tag}

SELECT * FROM ( SELECT COMMUNITY.COMM_ID, COMMUNITY.TAG, COMMUNITY.TITLE, USERS.NICKNAME, COMMUNITY.CREATE_DATE, COMMUNITY.RECOMMEND, COMMUNITY.VIEWS, ROW_NUMBER() OVER ( ORDER BY
COMMUNITY.RECOMMEND DESC, COMMUNITY.CREATE_DATE DESC
COMMUNITY.VIEWS DESC, COMMUNITY.CREATE_DATE DESC
COMMUNITY.CREATE_DATE DESC
) AS ROWNUMBER FROM COMMUNITY JOIN USERS ON COMMUNITY.USER_ID = USERS.USER_ID
(COMMUNITY.TITLE LIKE #{keyword} OR USERS.NICKNAME LIKE #{keyword})
AND COMMUNITY.TAG = #{tag}
) subquery WHERE ROWNUMBER BETWEEN #{offset} + 1 AND #{offset} + #{pageSize}

mapper.update:
UPDATE COMMUNITY SET
TITLE=#{title}, CAT=#{cat}, CTT=#{ctt}, TAG=#{tag}, IMAGE=#{image}
WHERE COMM_ID=#{comm_id}
