mapper.select:
SELECT * FROM (
SELECT
a.*, ROWNUM rnum
FROM (
SELECT * FROM POKEMON
WHERE
1=1

	AND CARD_SORT = #{card_sort}


	AND CARD_TYPE = #{card_type}


	AND CARD_NAME LIKE '%' || #{card_name} || '%'

ORDER BY CARD_ID
) a
WHERE ROWNUM <= #{endRow}
)
WHERE rnum >=
#{startRow}

