SELECT * FROM ( SELECT A.*, ROWNUM AS rn FROM ( SELECT d.deck_id, d.deck_title, d.create_date, u.user_id, d.recommend, p.card_image FROM decks d JOIN users u ON d.user_id = u.user_id JOIN deckSources ds ON d.deck_id = ds.deck_id JOIN pokemon p ON ds.p_card = p.card_id WHERE ds.is_thumbnail = 1 AND d.cat = #{cat}
AND d.deck_title LIKE '%' || #{query} || '%'
ORDER BY #{sort} DESC ) A WHERE ROWNUM <= #{endRow} ) WHERE rn >= #{startRow} ORDER BY #{sort} DESC
